  var versao = "Versão: 1.0.23.01.2019";
  var titulo = "TJRS - Biblioteca de scripts";

  javax.swing.JOptionPane
     .showMessageDialog
     (null, 
      versao,
      titulo,
      javax.swing.JOptionPane.INFORMATION_MESSAGE
     );
}

